import React, { useState, useEffect } from 'react';
import emailjs from '@emailjs/browser';
import '../styles/contactForm.css';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });
  const [status, setStatus] = useState('');
  const [isSending, setIsSending] = useState(false); // State to manage sending status
  const [isFormValid, setIsFormValid] = useState(false); // State to manage form validity

  // Function to check if all fields are filled
  useEffect(() => {
    setIsFormValid(formData.name !== '' && formData.email !== '' && formData.message !== '');
  }, [formData]);

  // Initialize EmailJS with your public key
  useEffect(() => {
    emailjs.init('LDwhGlJt1NBLPcH2n'); // Replace with your actual public key
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSending(true);
    setStatus('Verzenden...');

    const templateParams = {
      name: formData.name,
      email: formData.email,
      message: formData.message,
      reply_to: formData.email
    };

    emailjs
      .send(
        'service_z5zhhp7',     // Replace with your actual service ID from EmailJS
        'template_4y6sl7t',    // Replace with your actual template ID from EmailJS
        templateParams
      )
      .then(
        (response) => {
          setStatus('Bericht succesvol verzonden!');
          setFormData({ name: '', email: '', message: '' });
        },
        (error) => {
          setStatus('Fout bij het verzenden van het bericht. Probeer opnieuw.');
          console.error('EmailJS error:', error);
        }
      )
      .finally(() => {
        setIsSending(false);
      });
  };

  return (
    <div id="contact-form" className="contact-form py-10 px-6 lg:px-40 bg-slate-100">
      <h2 className="text-3xl font-bold text-center mb-8 uppercase">Contact</h2>
      <form onSubmit={handleSubmit} className="max-w-lg mx-auto space-y-4">
        <div>
          <label htmlFor="name" className="block text-lg font-semibold text-gray-700">
            Naam
          </label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-red-500"
            placeholder="Uw naam"
          />
        </div>
        <div>
          <label htmlFor="email" className="block text-lg font-semibold text-gray-700">
            E-mail
          </label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-red-500"
            placeholder="Uw e-mailadres"
          />
        </div>
        <div>
          <label htmlFor="message" className="block text-lg font-semibold text-gray-700">
            Bericht
          </label>
          <textarea
            id="message"
            name="message"
            value={formData.message}
            onChange={handleChange}
            required
            rows="5"
            className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-red-500"
            placeholder="Uw bericht"
          />
        </div>

  <button
  type="submit"
  className={`px-4 py-3 rounded-md font-semibold transition duration-300 ${
    isSending
      ? 'bg-gray-400 cursor-not-allowed text-white'
      : status.includes('succesvol')
      ? 'bg-blue-600 text-white hover:bg-blue-700'
      : status.includes('Fout')
      ? 'bg-red-600 text-white hover:bg-red-700'
      : isFormValid
      ? 'bg-green-600 text-white hover:bg-green-700'
      : 'bg-gray-300 text-gray-600 cursor-not-allowed'
  }`}
  disabled={isSending || !isFormValid}
>
  {isSending ? 'Verzenden...' : status.includes('succesvol') ? 'Verzonden' : 'Verstuur'}
</button>


        {status && (
          <p className={`text-center mt-4 ${
            status === 'Verzenden...' ? 'text-gray-600' : status.includes('succesvol') ? 'text-green-600' : 'text-red-600'
          }`}>
            {status}
          </p>
        )}


      </form>
    </div>
  );
};

export default ContactForm;
