import React, { useState } from 'react';
import card_windmolens from '../../images/card_windmolens.jpg';
import card_asielEnMigratie from '../../images/card_asiel&migratie.jpg';
import card_woningbouw from '../../images/card_woningbouw.jpg';

const ThreeCards = () => {
  const [selectedCard, setSelectedCard] = useState(null);

  const cards = [
    {
      category: 'Energie',
      title: 'Windmolens',
      text:
        'Het idee van windmolens spreekt tot de verbeelding en roept beelden op van onze Hollandse glorie. Toch is het traditionele, charmante...',
      image: card_windmolens,
      alt: 'Windmolens',
      bgColor: 'bg-blue-100',
      modalTitle: 'Windmolens in ons landschap',
      modalContent: `Het idee van windmolens spreekt tot de verbeelding en roept beelden op van onze Hollandse glorie. Toch is het traditionele, charmante molentje veranderd in een moderne toren van soms wel 320 meter hoog.\n\nMet knipperende lichten, draaiende wieken en geluid dat in sommige gevallen voor overlast zorgt, hebben deze windmolens een flinke impact op de omgeving — voor mens én dier.\n\nWij vinden duurzaamheid belangrijk, maar daar hoort ook aandacht bij voor leefbaarheid en een eerlijke afweging van kosten en baten, zowel financieel als op het gebied van CO₂-reductie.\n\nIn specifieke situaties, bijvoorbeeld als de molens worden ingezet voor de productie van waterstof, kan het zeker rendabel zijn.\n\nMaar in onze dorpen lijkt dat geen goede oplossing.`,
    },
    {
      category: 'Politiek',
      title: 'Asiel en Migratie',
      text:
        'Natuurlijk willen we allemaal barmhartig zijn, maar soms voelt het alsof de grenzen van wat een gemeenschap kan...',
      image: card_asielEnMigratie,
      alt: 'Asiel en Migratie',
      bgColor: 'bg-purple-200',
      modalTitle: 'Migratie en draagvlak',
      modalContent: `Natuurlijk willen we allemaal barmhartig zijn, maar soms voelt het alsof de grenzen van wat een gemeenschap kan dragen, bereikt zijn. In zulke gevallen is het belangrijk om samen naar passende oplossingen te zoeken.\n\nDe aankondiging van een asielcentrum voor 350 mannen — grotendeels met een kleine kans op een verblijfsstatus — in een Natura2000-gebied, met zelfs de optie tot uitbreiding naar 700 plekken, riep veel emoties op. Hier, en in veel andere gemeenten.\n\nWij geloven dat dorpen en kleinere gemeenschappen zelf zeggenschap moeten hebben over het aantal opvangplekken en de manier waarop mensen worden opgevangen.\n\nAlleen dan blijft er draagvlak — en ruimte voor echte menselijke aandacht.`,
    },
    {
      category: 'Huisvesting',
      title: 'Woningbouw',
      text:
        'Wonen in je dorp is als kind heerlijk, als tiener soms wat saai, maar als volwassene kom je er vaak met plezier weer terug.',
      image: card_woningbouw,
      alt: 'Woningbouw',
      bgColor: 'bg-white',
      modalTitle: 'Huisvesting voor dorpsbewoners',
      modalContent: `Wonen in je dorp is als kind heerlijk, als tiener soms wat saai, maar als volwassene kom je er vaak met plezier weer terug.\n\nTenminste — als daar nog plek voor is.\n\nDorpVoorDorp wil zich inzetten om de lokale bevolking voorrang te geven op de woningmarkt.\n\nDit kan onder meer door afspraken te maken met woningcorporaties, maar ook door specifieke woningtypes te bouwen die als koopwoning voor dorpsbewoners beschikbaar blijven.\n\nZo zorgen we ervoor dat jongeren kunnen blijven, gezinnen zich kunnen vestigen en het dorp zijn eigen kracht behoudt.`,
    },
  ];

  const openModal = (index) => {
    setSelectedCard(index);
    document.body.style.overflow = 'hidden';
  };

  const closeModal = () => {
    setSelectedCard(null);
    document.body.style.overflow = 'auto';
  };

  return (
    <div id='articles' className="bg-gray-300 md:p-20 flex gap-12 p-6 flex-wrap lg:flex-nowrap justify-center" onClick={closeModal}>
      {cards.map((card, index) => (
        <div
          key={index}
          onClick={(e) => {
            e.stopPropagation();
            openModal(index);
          }}
          className={`${card.bgColor} max-w-sm rounded-lg shadow-lg p-6 w-full sm:w-1/2 lg:w-1/3 flex flex-col items-start cursor-pointer`}
        >
          <span className="inline-block text-xs font-semibold text-gray-600 mb-2">{card.category}</span>


<article
  className="flex flex-col justify-between h-full" // Ensure the article takes full height and uses flex to space content
  onClick={() => {
    openModal(index);
  }}
>
  <div>
                <div className="inline-block bg-gray-300 rounded-lg overflow-hidden">
                <img
                  src={card.image}
                  alt={card.alt}
                  className="object-cover object-center w-full h-auto max-w-full"
                />
              </div>
    <h3 className="mt-4 mb-2 text-xl font-semibold text-gray-800">{card.title}</h3>
    <p className="text-sm text-gray-700">{card.text}</p>
  </div>

  {card.text.length > 104 && (
    <button
      className="mt-4 w-fit px-4 py-2 text-sm bg-gray-700 text-white rounded-lg flex items-center gap-2 hover:bg-gray-800"
      onClick={(e) => {
        e.stopPropagation();
        openModal(index);
      }}
    >
      <span>Verder lezen</span>
      <span className="mt-1">→</span>
    </button>
  )}
</article>
        </div>
      ))}

      {/* Modal */}
          {selectedCard !== null && (
        <div
          className="fixed inset-0 z-50 bg-black bg-opacity-70 flex justify-center pt-5 px-4"
          onClick={closeModal}
        >
          <div
            className="relative bg-white md:rounded-lg w-full md:max-w-md overflow-y-auto p-6"
            style={{
              height: 'fit-content', // Default height for desktop
              maxHeight: 'calc(100% - 60px)', // Adjusted height for mobile screens
            }}
            onClick={(e) => e.stopPropagation()}
          >
            {/* Close button for mobile */}
            <button
              className="md:hidden absolute top-4 right-4 text-2xl font-light text-gray-400 hover:text-gray-900"
              style={{ fontFamily: 'Arial, sans-serif', fontWeight: 100 }}
              onClick={closeModal}
            >
              ✕
            </button>
            <h2 className="mt-6 md:mt-0 pb-4 border-b text-2xl font-semibold mb-4 md:mb-6">
              {cards[selectedCard].modalTitle}
            </h2>
            <div className="text-gray-700 mb-5 md:border-b md:pb-6">
              {cards[selectedCard].modalContent.split('\n').map((line, index) =>
                line.trim() ? (
                  <p key={index} className="mb-2">
                    {line}
                  </p>
                ) : (
                  <div key={index} className="h-1" />
                )
              )}
            </div>
            <button
              className="px-6 py-2 bg-gray-700 text-white text-md rounded-lg hover:bg-gray-800"
              onClick={closeModal}
            >
              Sluiten
            </button>
          </div>
        </div>

      )}

    </div>
  );
};

export default ThreeCards;
