import React from 'react';
import landingImage from '../../images/landingImage.jpg';
import '../styles/sectionOne.css'; 

const SectionOne = () => {
  return (
    <div id='home' className="relative section_one mt-10 flex flex-col md:flex-row w-full h-auto bg-white">
      {/* Image Section */}
      <div className="relative w-full md:w-2/3 h-[100dvh]">
        <div className="relative w-full h-full">
          <img
            src={landingImage}
            alt="Village scene with table and flowers"
            className="w-full h-full object-cover"
          />
        </div>

        {/* Text Section for Small Screens */}
        <div className="absolute inset-0 flex items-center justify-center md:hidden text-center">
          <div className="dark_overlay bg-opacity-70 rounded-lg max-w-xs">
            <div className="title_and_text text-white py-10 px-6 z-10">
              <h2 className="section_title mb-4 text-2xl font-bold">DORP VOOR DORP</h2>
              <p className="mb-1 text-lg">
                Behoud onze gezelligheid.
              </p>
              <p>Samenwerken waar het kan, </p>
              <p>zelfstandig waar het moet</p>
            </div>
          </div>
        </div>

      </div>

      {/* Text Section for Larger Screens */}
      <div className="hidden rightPanel md:flex md:w-1/3 text-white pb-6 md:px-10 flex-col justify-center text-center">
        <h2 className="text-3xl xl:text-5xl font-bold mb-4">DORP VOOR DORP</h2>
        <p className="text-xl">
          Behoud onze gezelligheid. <br />
Samenwerken waar het kan, <br /> zelfstandig waar het moet
        </p>
      </div>
    </div>
  );
};

export default SectionOne;
